function o(a){const t=new URL(window.location.href);return t.searchParams.set("page",a),t.pathname+t.search}document.addEventListener("DOMContentLoaded",function(){document.body.addEventListener("click",function(a){const t=a.target.closest("[data-page]");if(t&&!t.disabled){const e=t.getAttribute("data-page");if(e){console.log("Переход на страницу:",e),t.tagName==="A"&&a.preventDefault(),window.location.href=o(e);return}}const n=a.target.closest("[data-nav-url]");if(n){const e=n.getAttribute("data-nav-url");if(e){console.log("Навигация по data-nav-url:",e),n.tagName==="A"&&a.preventDefault(),window.location.href=e;return}}})});
