document.addEventListener("DOMContentLoaded",()=>{const i=document.querySelectorAll(".size-btn");i.length>0&&i.forEach(a=>{a.addEventListener("click",()=>{i.forEach(u=>u.classList.remove("size-btn--active")),a.classList.add("size-btn--active")})});const n=document.querySelector(".quantity");if(n){const a=n.querySelector(".quantity__btn--minus"),u=n.querySelector(".quantity__btn--plus"),e=n.querySelector(".quantity__input");a.addEventListener("click",()=>{let t=parseInt(e.value,10);t>e.min&&(e.value=t-1)}),u.addEventListener("click",()=>{let t=parseInt(e.value,10);t<e.max&&(e.value=t+1)}),e.addEventListener("change",()=>{let t=parseInt(e.value,10);const c=parseInt(e.min,10),s=parseInt(e.max,10);t<c&&(e.value=c),t>s&&(e.value=s)})}});
