document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".admin-dashboard");if(!e)return;const n=e.querySelectorAll(".admin-nav-btn"),s=e.querySelectorAll(".admin-section");n.forEach(t=>{t.addEventListener("click",()=>{n.forEach(a=>a.classList.remove("active")),s.forEach(a=>{a.style.display="none"}),t.classList.add("active");const o=t.dataset.target,c=e.querySelector(`#${o}`);c&&(c.style.display="block")})})});
